import Foundation
import UIKit
import ImagePreview

class ImagePreviewService {
  
  let header: NSData
  
  static let sharedInstance = ImagePreviewService(base64header: "")
  
  init(base64header: String) {
    header = NSData(base64EncodedString: base64header, options: [])!
  }
  
  func imageFromBody(base64body: String) -> UIImage? {
    return NSData(base64EncodedString: base64body, options: [])
      .flatMap { UIImage(header: header, body: $0) }
  }
  
}
